import React, { useContext, useState, useEffect } from "react";
import classes from "./HeaderCartButton.module.css";
import CartContext from "../store/cart-context";
import CartIcon from "../Cart/Carticon";

const HeaderCartbutton = (props) => {
  const cartCtx = useContext(CartContext);
  const {items}=cartCtx
  const TotalNoOfcartItems = items.reduce((currValue, item) => {
    return currValue + item.amount;
  }, 0);

  const [btnbump, setbtnbump] = useState();

  const BtnClasses = `${classes.button}${setbtnbump ? classes.bump : ""}`;

  useEffect =
    (() => {
      if(items.length>0)
      setbtnbump(true);
    },
    []);

  return (
    <div>
      <button className={BtnClasses} onClick={props.onClick}>
        <span className={classes.icon}>
          <CartIcon />
        </span>
        <span>Your Cart</span>
        <span className={classes.badge}>{TotalNoOfcartItems}</span>
      </button>
    </div>
  );
};

export default HeaderCartbutton;
