  //   if(items.length>0)
  //     setbtnbump(true)
  //     const timer=setTimeout(() => {
  //       setbtnbump(false)
        
  //     }, 300);
  //     return ()=>
  //     {
  //       clearTimeout(timer)
  //     }
  // },[items])

  return (
    <div>
      <button className={classes.button} onClick={props.onClick}>
        <span className={classes.icon}>
        {/* <FontAwesomeIcon icon="fa-solid fa-cart-shopping" />
         */}
         <CartIcon/>
        </span>
        <span>Your Cart</span>
        <span className={classes.badge}>{TotalNoOfcartItems}</span>
      </button>
    </div>
  );
};

export default HeaderCartbutton;
